import React, { useState } from "react"

// let btnNotClicked = false
const Table = props => {
  const [buttonText, setButtonText] = useState("Not Arrived ")
  // const [color, setColor] = useState("red")

  // const clickHandle = e => {
  //   setButtonText("seated")
  //   setColor("green")

  //   if (e.target.type === "checkbox" && !e.target.checked && e.target.n) {
  //     setButtonText("Not Arrived")
  //   } else {
  //     setButtonText("seated")
  //   }
  // }

  return (
    <div className="listDetails">
      <div className="title">
        <h3>Booking List</h3>
      </div>

      <table className="myTable">
        <thead>
          <tr>
            <th>FirstName</th>
            <th>LastName</th>
            <th>Email</th>
            <th>Tel Number</th>
            <th>Cover</th>
            <th>Date</th>
            <th>Time</th>
            <th>confirm</th>
            {/* <th>{props.randomID}</th> */}
          </tr>
        </thead>

        <tbody>
          {props.bookingList.map(value => {
            return (
              <tr className="rows" key={value.id}>
                <td>{value.firstName}</td>
                <td>{value.lastName}</td>
                <td>{value.email}</td>
                <td>{value.telNumber}</td>
                <td>{value.cover}</td>
                <td>{value.date}</td>
                <td>{value.time}</td>

                <td>
                  {/* <input
                    onChange={e => {
                      let checked = e.target.checked
