import React, { useState } from "react"

// let btnNotClicked = false
const Table = props => {
  const [buttonText, setButtonText] = useState("Not Arrived ")
  const [color, setColor] = useState("red")

  const clickHandle = e => {
    // setButtonText("seated")
    setColor("green")

    if (e.target.type === "checkbox" && !e.target.checked && e.target.n) {
      setButtonText("Not Arrived")
    } else {
      setButtonText("seated")
    }
  }

  return (
    <div className="listDetails">
      <div className="title">
        <h3>Booking List</h3>
      </div>

      <table className="myTable">
        <thead>
          <tr>
            <th>FirstName</th>
            <th>LastName</th>
            <th>Email</th>
            <th>Tel Number</th>
            <th>Cover</th>
            <th>Date</th>
            <th>Time</th>
            <th>Checked</th>
          </tr>
        </thead>

        <tbody>
          {props.bookingList.map(value => {
            return (
              <tr className="rows" id={value.id}>
                <td>{value.firstName}</td>
                <td>{value.lastName}</td>
                <td>{value.email}</td>
                <td>{value.telNumber}</td>
                <td>{value.cover}</td>
                <td>{value.date}</td>
                <td>{value.time}</td>
                <td>
                  <input
                    onChange={e => {
                      setButtonText("seated")

                      let checked = e.target.checked
                      props.setBookingList(
                        props.bookingList.map(data => {
                          if (value.id === data.id) {
                            data.select = checked
                          }
                          return data
                        })
                      )
                    }}
                    type="checkbox"
                    checked={value.select}
                  />
                  <label>{buttonText}</label>
                </td>
                {/* <td> */}
                {/* <button
                    style={{ color: color }}
                    onClick={() => {
                      setButtonText("seated")
                      setColor("green")
                    }}
                  >
                    {buttonText}
                  </button> */}
                {/* <button style={{ color: color }} onClick={clickHandle}>
                    {buttonText}
                  </button> */}
                {/* <input type="checkbox" onChange={clickHandle} />
                  <label>{buttonText}</label>
                </td> */}
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}

export default Table
